import { createSlice } from "@reduxjs/toolkit";

const initialOrderState = {
  food: "waiting",
  drink: "waiting",
  deliver: "waiting",
};

const orderStateSlice = createSlice({
  name: "orderState",
  initialState: initialOrderState,
  reducers: {
    startFoodPrep(state) {
      state.food = "preparing";
    },
    foodPrepared(state) {
      state.food = "prepared";
    },
    startDrinkPrep(state) {
      state.drink = "preparing";
    },
    drinkPrepared(state) {
      state.drink = "prepared";
    },
    deliver(state) {
      state.deliver = "ready";
    },
  },
});

export const orderStateActions = orderStateSlice.actions;
export default orderStateSlice.reducer;
