import Cookies from "js-cookie";

const csrftoken = Cookies.get("csrftoken");

export const updateOrderStatus = (status) => {
  return async () => {
    const sendRequest = async () => {
      const response = await fetch("/order/", {
        method: "PUT",
        headers: {
          "X-CSRFToken": csrftoken,
        },
        body: JSON.stringify({
          status: {
            food: status.food,
            drink: status.drink,
            deliver: status.deliver,
          },
        }),
      });
      if (!response) {
        throw new Error("Updating order status failed.");
      }
    };

    try {
      await sendRequest();
    } catch (error) {
      console.log(error);
    }
  };
};
