import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Card from "../UI/Card";
import classes from "Notification.module.css";

const Notification = () => {
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    const fetchStaffPerson = async () => {
      const response = await fetch("/staff/");

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const responseData = await response.json();
      const newestNotifications = responseData.slice(-10);

      const loadedNotifications = [];
      for (let i in newestNotifications) {
        loadedNotifications.push({
          role: newestNotifications[i].role,
          name: newestNotifications[i].name,
          notification: newestNotifications[i].notification,
        });
      }
      console.log(loadedNotifications);
      setNotifications(loadedNotifications);
    };

    fetchStaffPerson()
      .then()
      .catch((err) => console.log(err));
  }, []);

  const notificationsList = notifications.map((notification) => {
    return (
      <li className={classes.items}>
        <span>
          {notification.role}
          {notification.name} says {notification.notification}
        </span>
      </li>
    );
  });

  return (
    <Card>
      <section className={classes.container}>
        <h2>Notifications</h2>
        <div className={classes.notifications}>
          <ul>{notificationsList}</ul>
        </div>
      </section>
    </Card>
  );
};

export default Notification;
