import { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Card from "../UI/Card";
import classes from "./ProfileForm.module.css";
import { orderStateActions } from "../../store/order";
import { updateOrderStatus } from "../../store/order-actions";
import { updateNotification } from "../../store/staff-actions.js";

const OrderSummary = () => {
  const [orderedItems, setOrderedItems] = useState([]);
  const [price, setPrice] = useState(0);
  const [orderId, setOrderId] = useState(0);
  const [orderStatus, setOrderStatus] = useState({});
  const role = useSelector((state) => state.staff.role);
  const dispatch = useDispatch();
  const foodPrep = useSelector((state) => state.orderState.food);
  const drinkPrep = useSelector((state) => state.orderState.drink);
  const deliver = useSelector((state) => state.orderState.deliver);
  const [foodPrepCheck, setFoodPrepCheck] = useState(false);
  const [drinkPrepCheck, setDrinkPrepCheck] = useState(false);
  const [deliverCheck, setDeliverCheck] = useState(false);

  const [waiter, setWaiter] = useState(null);

  useEffect(() => {
    const fetchWaiter = async () => {
      const response = await fetch("/staff/");

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const responseData = await response.json();
      const waiter = responseData
        .filter((person) => person.role === "Waiter")
        .slice(-1)[0];
      console.log(waiter);
      setWaiter(waiter);
    };

    fetchWaiter()
      .then()
      .catch((err) => console.log(err));
  }, []);

  const handleFoodPrep = () => {
    if (!foodPrepCheck) {
      setFoodPrepCheck(true);
      dispatch(orderStateActions.startFoodPrep());
      dispatch(
        updateOrderStatus(
          {
            food: "preparing",
            drink: drinkPrep,
            deliver: deliver,
          },
          orderId
        )
      );
    }
    if (foodPrepCheck) {
      setFoodPrepCheck(false);
      dispatch(orderStateActions.foodPrepared());
      dispatch(
        updateOrderStatus(
          {
            food: "prepared",
            drink: drinkPrep,
            deliver: deliver,
          },
          orderId
        )
      );
      dispatch(updateNotification("Food Prepared"));
    }
  };

  const handleDrinkPrep = () => {
    if (!drinkPrepCheck) {
      setDrinkPrepCheck(true);
      dispatch(orderStateActions.startDrinkPrep());
      dispatch(
        updateOrderStatus(
          {
            food: foodPrep,
            drink: "preparing",
            deliver: deliver,
          },
          orderId
        )
      );
    }
    if (drinkPrepCheck) {
      setDrinkPrepCheck(false);
      dispatch(orderStateActions.drinkPrepared());
      dispatch(
        updateOrderStatus(
          {
            food: foodPrep,
            drink: "prepared",
            deliver: deliver,
          },
          orderId
        )
      );
    }
  };

  const handleDeliver = () => {
    if (meals.length !== 0 && drinks.length !== 0) {
      if (
        orderStatus.food === "prepared" &&
        orderStatus.drink === "prepared" &&
        role === "Waiter"
      ) {
        alert("Order is prepared");
        setDeliverCheck(true);
      }
    } else if (meals.length === 0 || drinks.length === 0) {
      if (
        orderStatus.food === "prepared" ||
        (orderStatus.drink === "prepared" && role === "Waiter")
      ) {
        alert("Order is prepared");
        setDeliverCheck(true);
      }
    }
    dispatch(orderStateActions.deliver());
    dispatch(
      updateOrderStatus(
        {
          food: foodPrep,
          drink: drinkPrep,
          deliver: "delivered",
        },
        orderId
      )
    );
    console.log("delivered");
  };

  useEffect(() => {
    const fetchOrderedItems = async () => {
      const response = await fetch("/order/");

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const responseData = await response.json();
      const lastOrder = responseData.slice(-1)[0];

      setOrderId(lastOrder.id);
      setOrderStatus(lastOrder.status);

      const loadedItems = [];
      for (let i in lastOrder.order_items) {
        loadedItems.push({
          id: i,
          name: lastOrder.order_items[i].name,
          quantity: lastOrder.order_items[i].quantity,
          category: lastOrder.order_items[i].category,
        });
      }
      setOrderedItems(loadedItems);
      setPrice(lastOrder.price);
    };

    fetchOrderedItems()
      .then()
      .catch((err) => console.log(err));
  }, []);

  const meals = orderedItems.filter((item) => item.category === "Food");
  const drinks = orderedItems.filter((item) => item.category === "Drink");

  const foodList = meals.map((item) => {
    return (
      <li className={classes.item} key={item.id}>
        <div>
          <span className={classes.name}>{item.name}</span>
          <span className={classes.quantity}>x {item.quantity}</span>
        </div>
      </li>
    );
  });

  const drinkList = drinks.map((item) => {
    return (
      <li className={classes.item} key={item.id}>
        <div>
          <span className={classes.name}>{item.name}</span>
          <span className={classes.quantity}>x {item.quantity}</span>
        </div>
      </li>
    );
  });

  const waiterDashboard = (
    <div className={classes.background}>
      <div className={classes.order}>
        <Card>
          {foodList.length !== 0 && (
            <div>
              <h3>Food</h3>
              <ul>{foodList}</ul>
            </div>
          )}
          {drinkList.length !== 0 && (
            <div>
              <h3>Drinks</h3>
              <ul>{drinkList}</ul>
            </div>
          )}
          <div className={classes.total}>
            <span>Total Amount</span>
            <span>$ {price}</span>
          </div>
          <button
            disabled={!deliverCheck}
            className={classes.form}
            onClick={handleDeliver}
          >
            Deliver
          </button>
        </Card>
      </div>
    </div>
  );

  const barmanDashboard = (
    <div className={classes.background}>
      <div className={classes.order}>
        <Card>
          {drinkList.length !== 0 && (
            <div>
              <div>
                <label>
                  <input
                    type="checkbox"
                    checked={drinkPrepCheck}
                    onChange={handleDrinkPrep}
                  />
                  Drinks
                </label>
              </div>
              <ul>{drinkList}</ul>
            </div>
          )}
        </Card>
      </div>
    </div>
  );

  const chefDashboard = (
    <div className={classes.background}>
      <div className={classes.order}>
        <Card>
          {foodList.length !== 0 && (
            <div>
              <div>
                <label>
                  <input
                    type="checkbox"
                    checked={foodPrepCheck}
                    onChange={handleFoodPrep}
                  />
                  Food
                </label>
              </div>
              <ul>{foodList}</ul>
            </div>
          )}
        </Card>
      </div>
    </div>
  );

  return (
    <section className={classes.profile}>
      <h1>Order #{orderId}</h1>
      {role === "Barman" && barmanDashboard}
      {role === "Waiter" && waiterDashboard}
      {role === "Chef" && chefDashboard}
    </section>
  );
};

export default OrderSummary;
