import { Fragment, useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Card from "../UI/Card";
import classes from "./ProfileForm.module.css";

const OrderSummary = () => {
  const [orderedItems, setOrderedItems] = useState([]);
  const [price, setPrice] = useState(0);
  const [orderId, setOrderId] = useState(0);
  const person = useSelector((state) => state.staff.person);
  console.log("role", role);

  useEffect(() => {
    const fetchOrderedItems = async () => {
      const response = await fetch("/order/");

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const responseData = await response.json();
      const lastOrder = responseData.slice(-1)[0];
      console.log(lastOrder);

      setOrderId(lastOrder.id);

      const loadedItems = [];
      for (let i in lastOrder.order_items) {
        loadedItems.push({
          id: i,
          name: lastOrder.order_items[i].name,
          quantity: lastOrder.order_items[i].quantity,
          category: lastOrder.order_items[i].category,
        });
      }
      setOrderedItems(loadedItems);
      setPrice(lastOrder.price);
    };

    fetchOrderedItems()
      .then()
      .catch((err) => console.log(err));
  }, []);

  const meals = orderedItems.filter((item) => item.category === "Food");
  const drinks = orderedItems.filter((item) => item.category === "Drink");

  const foodList = meals.map((item) => {
    return (
      <li className={classes.item} key={item.id}>
        <div>
          <span className={classes.name}>{item.name}</span>
          <span className={classes.quantity}>x {item.quantity}</span>
        </div>
      </li>
    );
  });

  const drinkList = drinks.map((item) => {
    return (
      <li className={classes.item}>
        <div>
          <span className={classes.name}>{item.name}</span>
          <span className={classes.quantity}>x {item.quantity}</span>
        </div>
      </li>
    );
  });

  return (
    <Fragment>
      <div className={classes.background}>
        <div className={classes.order}>
          <Card>
            <h2>Order #{orderId}</h2>
            {foodList.length !== 0 && (
              <div>
                <h3>Food</h3>
                <ul>{foodList}</ul>
                <button className={classes.form}>Send to chef</button>
              </div>
            )}
            {drinkList.length !== 0 && (
              <div>
                <h3>Drinks</h3>
                <ul>{drinkList}</ul>
                <button className={classes.form}>Send to barmen</button>
              </div>
            )}
            <div className={classes.total}>
              <span>Total Amount</span>
              <span>$ {price}</span>
            </div>
            <button className={classes.form}>Deliver</button>
          </Card>
        </div>
      </div>
    </Fragment>
  );
};

export default OrderSummary;
