import { configureStore } from "@reduxjs/toolkit";
import staffReducer from "./staff";
import orderStateReducer from "./order";

const store = configureStore({
  reducer: {
    staff: staffReducer,
    orderState: orderStateReducer,
  },
});

export default store;
